import { useState } from "react";

// Importing all components
import Header from "./components/Header";
import MessageInput from "./components/MessageInput";
import PredictionResult from "./components/PredictionResult";
import AlertOverlay from "./components/AlertOverlay";
import LimeExplanation from "./components/LimeExplanation";
import SafeReply from "./components/SafeReply";

function App() {
  const [message, setMessage] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message }),
      });

      if (!response.ok) {
        throw new Error("Server error. Check your backend.");
      }

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Error fetching prediction:", error);
      setResult({ error: "Failed to connect to backend" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className={
        darkMode
          ? "bg-gray-900 text-white min-h-screen"
          : "bg-gray-100 text-gray-900 min-h-screen"
      }
    >
      <div className="container mx-auto px-6 py-10 flex flex-col items-center">
        {/* ‚úÖ Header */}
        <Header darkMode={darkMode} setDarkMode={setDarkMode} />

        {/* ‚úÖ Message Input */}
        <MessageInput
          message={message}
          setMessage={setMessage}
          handleSubmit={handleSubmit}
          loading={loading}
        />

        {/* ‚úÖ Prediction Result */}
        {result && <PredictionResult result={result} />}

        {/* ‚úÖ Alert Overlay (show if scam detected) */}
        {result?.prediction === "scam" && <AlertOverlay />}

        {/* ‚úÖ LIME Explanation */}
        {result && <LimeExplanation explanation={result.explanation} />}

        {/* ‚úÖ Safe Reply Suggestion */}
        {result && <SafeReply suggestion={result.safeReply} />}
      </div>
    </div>
  );
}

export default App;
