import React from "react";
import Header from "./components/Header";
import AlertOverlay from "./components/AlertOverlay";
import LimeExplanation from "./components/LimeExplanation";
import MessageInput from "./components/MessageInput";
import PredictionResult from "./components/PredictionResult";
import SafeReply from "./components/SafeReply";

// Small helper to stop components from crashing if props are missing
const SafeWrapper = ({ children }) => {
  try {
    return children;
  } catch (err) {
    console.error("Component error:", err);
    return <div className="text-red-500">‚ö†Ô∏è Error rendering component</div>;
  }
};

export default function App() {
  return (
    <div className="p-6 space-y-6">
      <SafeWrapper>
        <Header />
      </SafeWrapper>

      <SafeWrapper>
        <AlertOverlay
          show={true}
          onClose={() => alert("Closed overlay!")}
          message="‚ö†Ô∏è This is a test alert overlay!"
        />
      </SafeWrapper>

      <SafeWrapper>
        <MessageInput onSend={(msg) => alert("Message sent: " + msg)} />
      </SafeWrapper>

      <SafeWrapper>
        <PredictionResult prediction={"ham"} confidence={0.92} />
      </SafeWrapper>

      <SafeWrapper>
        <SafeReply
          replies={[
            "Thanks for your message!",
            "We‚Äôll get back to you shortly.",
          ]}
        />
      </SafeWrapper>

      <SafeWrapper>
        <LimeExplanation
          message="Congratulations! You win a prize!"
          explanation={[
            ["win", 0.45],
            ["prize", 0.33],
          ]}
        />
      </SafeWrapper>
    </div>
  );
}
