import React from "react";

const ResultBox = ({ result }) => {
  if (!result) return null; // Don’t render if no result

  return (
    <div
      style={{
        marginTop: "20px",
        padding: "10px",
        borderRadius: "5px",
        backgroundColor: result.toLowerCase().includes("invalid")
          ? "#ffcccc" // red for invalid
          : "#ccffcc", // green for valid
      }}
    >
      <strong>Result:</strong> {result}
    </div>
  );
};

export default ResultBox;
