import { useState, useEffect } from "react";
import Header from "./components/Header";
import MessageInput from "./components/MessageInput";
import PredictionResult from "./components/PredictionResult";
import SafeReply from "./components/SafeReply";
import LimeExplanation from "./components/LimeExplanation";
import AlertOverlay from "./components/AlertOverlay";

function App() {
  const [result, setResult] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [history, setHistory] = useState([]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) document.documentElement.classList.add("dark");
    else document.documentElement.classList.remove("dark");
  };

  useEffect(() => {
    if (result && (result.prediction === "spam" || result.prediction === "scam")) {
      setShowAlert(true);
      const timer = setTimeout(() => setShowAlert(false), 4000);
      return () => clearTimeout(timer);
    }
    if (result) {
      setHistory([result, ...history]);
    }
  }, [result]);

  const safeSuggestions = [
    "Thank you, I will get back to you soon.",
    "Noted. Will check and reply shortly.",
    "Received. I will respond after reviewing."
  ];

  return (
    <div className="min-h-screen flex flex-col items-center p-6 transition-colors duration-500 relative">
      <AlertOverlay show={showAlert} />

      <button
        onClick={toggleDarkMode}
        className="absolute top-6 right-6 bg-gray-300 dark:bg-gray-700 text-gray-800 dark:text-gray-200 px-3 py-1 rounded-lg shadow-md"
      >
        {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
      </button>

      <Header />
      <MessageInput setResult={setResult} />
      {result && (
        <>
          <PredictionResult result={result} />
          {result.prediction !== "spam" && <SafeReply suggestions={safeSuggestions} />}
          <LimeExplanation explanation={result.explanation} message={result.message} />
        </>
      )}

      {/* Optional: History Section */}
      {history.length > 0 && (
        <div className="mt-6 w-full max-w-xl">
          <h3 className="font-semibold mb-2 text-gray-700 dark:text-gray-200">History</h3>
          {history.map((h, idx) => (
            <div key={idx} className="p-3 bg-gray-100 dark:bg-gray-800 rounded mb-2 shadow-sm">
              <p className="text-sm">{h.message}</p>
              <p className="text-xs mt-1 text-gray-500 dark:text-gray-400">{h.prediction} - {h.confidence}%</p>
            </div>
          ))}
        </div>
      )}

      <footer className="mt-12 text-gray-500 dark:text-gray-400 text-sm">
        SecureText AI ¬© 2025 | Powered by Open-Source ML
      </footer>
    </div>
  );
}

export default App;



