import React from "react";

const ResultBox = ({ result }) => {
  if (!result) return null; // Don’t render if no result

  // make sure it's always a string
  const safeResult = String(result);

  return (
    <div
      style={{
        marginTop: "20px",
        padding: "10px",
        borderRadius: "5px",
        backgroundColor: safeResult.toLowerCase().includes("invalid")
          ? "#ffcccc" // red for invalid
          : "#ccffcc", // green for valid
      }}
    >
      <strong>Result:</strong> {safeResult}
    </div>
  );
};

export default ResultBox;
