import { motion } from "framer-motion";
import { ShieldCheck, AlertTriangle } from "lucide-react";

export default function ResultBox({ result }) {
  if (!result) return null; // Don't show if there's no result yet

  const isSpam = result.toLowerCase() === "spam";

  return (
    <motion.div
      className={`mt-6 p-6 rounded-2xl shadow-lg text-center font-semibold text-lg w-full max-w-xl mx-auto ${
        isSpam ? "bg-red-100 text-red-700" : "bg-green-100 text-green-700"
      }`}
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="flex items-center justify-center gap-3">
        {isSpam ? (
          <>
            <AlertTriangle className="w-6 h-6 text-red-600" />
            <span>ðŸš¨ Spam Detected!</span>
          </>
        ) : (
          <>
            <ShieldCheck className="w-6 h-6 text-green-600" />
            <span>âœ… Safe Message</span>
          </>
        )}
      </div>
    </motion.div>
  );
}
